import { gameData, GameData, cpMultipliers, CpMultipliers, typeEffectiveness, TypeEffectiveness, PokemonInfo } from './data';

export class Pokemon {
  data: PokemonInfo;
  constructor(speciesId: string, level: number, atkIV: number, defIV: number, hpIV: number, fastMove: string, chargedMoves: string[], shields: number) {
    this.data = gameData.pokemon.filter((pokemon) => pokemon.speciesId === speciesId)[0];
    this.data.level = level;
    this.data.atkIV = atkIV;
    this.data.defIV = defIV;
    this.data.hpIV = hpIV;
    this.data.currentFastMove = searchAttack(fastMove);
    this.data.currentChargedMoves = chargedMoves.map((move) => searchAttack(move));
    this.data.shields = shields;
    this.data.currentMove = null;
    this.data.energy = 0;
    this.data.buffs = [0,0];
    this.calculateCP();
    this.calculateStatsAtLevel();
  }

  calculateCP(): void {
    const weightedAttack = this.data.baseStats.atk+this.data.atkIV;
    const weightedDefense = Math.sqrt(this.data.baseStats.def+this.data.defIV);
    const weightedHp = Math.sqrt(this.data.baseStats.hp+this.data.hpIV);
    const cpMultiplier = getCpMultiplier(this.data.level);
    const weightedCpMultiplier = Math.pow(cpMultiplier, 2);
    const unFlooredCP = weightedAttack * weightedDefense * weightedHp * weightedCpMultiplier * 0.1
    this.data.cp = Math.max(Math.floor(unFlooredCP), 10);
  }

  calculateStatsAtLevel(): void {
    const result = [];
    const cpMultiplier = this.getCpMultiplier(this.data.level);
    result.push(truncateOneDecimal(cpMultiplier * (this.data.baseStats.atk+this.data.atkIV)));
    result.push(truncateOneDecimal(cpMultiplier * (this.data.baseStats.def+this.data.defIV)));
    result.push(Math.floor(cpMultiplier * (this.data.baseStats.hp+this.data.hpIV)));
    this.data.atk = result[0];
    this.data.def = result[1];
    this.data.hp = result[2];
    this.data.currentHp = result[2];
  };

  getCpMultiplier(level: number): number {
    return cpMultipliers[level*2-2];
  }

  isShadow(): boolean {
    return this.data.speciesId.endsWith('_shadow') ? true : false;
  };
}










export const calculateMoveEffectiveness = (attackType, pokemonTypes) => {
  let baseEffectiveness = 1;
  let attackTE = typeEffectiveness.filter((type) => type.name === attackType)[0];
  for(let type of pokemonTypes) {
    if(attackTE.immunes.includes(type)) {
      baseEffectiveness *= 0.390625;
    } else if(attackTE.weaknesses.includes(type)) {
      baseEffectiveness *= 0.625;
    } else if (attackTE.strengths.includes(type)) {
      baseEffectiveness *= 1.6;
    }
  }
  return baseEffectiveness;
};

export const truncateOneDecimal = (number) => {
  let truncatedNumber = Math.trunc(number*10);
  return truncatedNumber / 10;
}

export const calculateAttackDamage = (attackingPokemon, attack, opposingPokemon) => {
  const hasStab = attackingPokemon.types.includes(attack.type);
  const stab = hasStab ? 1.2 : 1;
  const bonusMultiplier = 1.3;
  return Math.floor(0.5 * attack.power * ( getStat(attackingPokemon, 0) / getStat(opposingPokemon, 1)) * stab * calculateMoveEffectiveness(attack.type, opposingPokemon.types) * bonusMultiplier) + 1;
};

export const searchAttack = (attackName) => {
  return gameData.moves.filter((move) => move.name === attackName)[0];
}

export const getStat = (pokemon, stat) => {
  if(stat == 0){
    return pokemon.atk * (isShadow(pokemon.speciesId) ? (1.2) : 1) * getBuffMultiplier(pokemon, 0);
  } else if(stat == 1){
    return pokemon.def * (isShadow(pokemon.speciesId) ? (5/6) : 1) * getBuffMultiplier(pokemon, 1);
  }
}

export const getBuffMultiplier = (pokemon, stat) => {
  if(pokemon.buffs[stat] > 0) {
    return (4 + pokemon.buffs[stat]) / 4;
  } else{
    return 4 / (4 - pokemon.buffs[stat]);
  }
}
